import { AfterViewInit, Component, ViewChild } from '@angular/core';
import {
  ScannerQRCodeConfig,
  ScannerQRCodeResult,
  NgxScannerQrcodeService,
  NgxScannerQrcodeComponent,
  ScannerQRCodeSelectedFiles,
} from 'ngx-scanner-qrcode';
import * as data from './data.json';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements AfterViewInit {
  title = "DieLess";
  public dogData: any = (data as any).default;
  public selectedDog: any;

  // https://developer.mozilla.org/en-US/docs/Web/API/MediaDevices/getUserMedia#front_and_back_camera
  public config: ScannerQRCodeConfig = {
    constraints: {
      video: {
        width: window.innerWidth,
        height: window.innerHeight
      },
    },
   
    canvasStyles: [
      { /* layer */
        lineWidth: 1,
        /*85 represents transparency*/
        fillStyle: '#00949485',
        strokeStyle: '#00949485',
      },
      { /* text */
        font: '17px serif',
        fillStyle: '#ffffff',
        strokeStyle: '#ffffff',
      }
    ],
    isBeep: false,
    fps:30,
    src:'../assets/cat1.jpg'
  };
  
  //holds results
  public qrCodeResult: ScannerQRCodeSelectedFiles[] = [];
  public qrCodeResult2: ScannerQRCodeSelectedFiles[] = [];

  //reference to the component
  @ViewChild('action') action!: NgxScannerQrcodeComponent;

  //injects the service
  constructor(private qrcode: NgxScannerQrcodeService) { }

  //coment the handle to start automatically
  ngAfterViewInit(): void {
    this.action.isReady.subscribe((res: any) => {
       this.handle(this.action, 'start');
    });
  }

  //when a event is emited to the scanner logs it into the console
  //the line above console pauses once it gets a qr
  public onEvent(e: ScannerQRCodeResult[], action?: any): void {
    if (e && action) {
      action.pause();
      console.log(e); // This logs the scanned QR code data to the console
  
      // Convert the Int8Array to a string
      let decoder = new TextDecoder();
      let id = decoder.decode(new Uint8Array(e[0].data));
      console.log(id); // This logs the ID to the console

      // Find the dog data associated with the scanned ID
      this.selectedDog = this.dogData.find((dog: any) => dog.id === id);

      // Log the selected dog data to the console
      console.log(this.selectedDog);
    }
  }

  //USED TO START STOP PLAY OR PAUSE THE SCANNER
  public handle(action: any, fn: string): void {
    const playDeviceFacingBack = (devices: any[]) => {
      // front camera or back camera check here!
      const device = devices.find(f => (/back|rear|environment/gi.test(f.label))); // Default Back Facing Camera
      action.playDevice(device ? device.deviceId : devices[0].deviceId);
    }

    if (fn === 'start') {
      action[fn](playDeviceFacingBack).subscribe((r: any) => console.log(fn, r), alert);
    } else {
      action[fn]().subscribe((r: any) => console.log(fn, r), alert);
    }
  }

  //merged method
  public handleStartOrUnpause(action: any): void {
    if (!action.isStart) {
      // If the scanner is not started, start it
      this.handle(action, 'start');
    } else if (action.isPause) {
      // If the scanner is started but paused, unpause it
      this.handle(action, 'play');
    }
  }

  //to upload files meh
  public onSelects(files: any) {
    this.qrcode.loadFiles(files, 0.5).subscribe((res: ScannerQRCodeSelectedFiles[]) => {
      this.qrCodeResult = res;
    });
  }

  public onSelects2(files: any) {
    this.qrcode.loadFilesToScan(files, this.config, 0.5).subscribe((res: ScannerQRCodeSelectedFiles[]) => {
      console.log(res);
      this.qrCodeResult2 = res;
    });
  }
}

<html>
  <body style="background-color: #c2d1ff">
    <div>
      <section class="py-6">
        <div class="container">
          <div class="row">
            <div class="col-sm-12 text-center">
              <h2 class="sm-3">DIELESS</h2>
              <h4 class="sm-4 secondary_color">
                Bienvenidos a tu portal de plantillas de vacunación DieLess.
                <br />
                Nuestro sistema innovador le permite escanear un código QR para
                obtener información detallada sobre la vacunación de su perro.
              </h4>

              <div class="col-sm-12 photo_qr_card" [hidden]="action.isPause">
                <!-- <img src="..\assets\cat1.jpg" alt="cat1" class="img-fluid" /> -->
                <ngx-scanner-qrcode
                  #action="scanner"
                  [config]="config"
                  (event)="onEvent($event, action)"
                ></ngx-scanner-qrcode>

                <!-- data  -->
                <!-- <p class="data">{{ (action.data | async | json) }}</p> -->

                <!-- loading -->
                <p *ngIf="action.isLoading">⌛ Loading...</p>
              </div>
            </div>

            <div class="col-sm-12">
              <div class="mt-5">
                <button
                  class="button btn-primary btn-px-2 btn-lg m-auto"
                  [class.btn-info]="!action.isStart"
                  [class.btn-warning]="action.isStart"
                  [disabled]="action.isLoading"
                  (click)="handleStartOrUnpause(action)"
                >
                  <span> ¡Escanear OTRO Codigo!</span>
                </button>
              </div>
            </div>

            <div class="card" *ngIf="selectedDog">
              <div class="card-body">
                <h2 class="card-title">{{ selectedDog.nombreDelPerro }}</h2>
                <img
                  [src]="selectedDog.imageUrl"
                  alt="Dog image"
                  class="img-fluid"
                />
                <p class="card-text">
                  <strong>Descripción:</strong>
                  {{ selectedDog.descripcionDelPerro.color }},
                  {{ selectedDog.descripcionDelPerro.tamano }}<br />
                  <strong>Edad:</strong>
                  {{ selectedDog.edadAnosdelPerro }} years<br />
                  <strong>Raza:</strong> {{ selectedDog.raza }}<br />
                  <strong>Fecha de Última Atención:</strong>
                  {{ selectedDog.fechaDeUltimaAtencion }}<br />
                  <strong>Género:</strong> {{ selectedDog.genero }}<br />
                  <strong>Esterilización:</strong>
                  {{ selectedDog.esterilizacion }}<br />
                  <strong>Dueño:</strong> {{ selectedDog.nombreDueno }}<br />
                  <strong>Contacto:</strong>
                  {{ selectedDog.numeroDeTelefonoDelDueno }}
                </p>

                <div class="card-text text-center">
                  <strong>Cartilla de Vacunación:</strong>
                  <table class="table table-bordered text-center">
                    <thead>
                      <tr>
                        <th>Nombre de la Vacuna</th>
                        <th>Fecha</th>
                        <th>Próxima Fecha</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr *ngFor="let vacuna of selectedDog.cartillaDeVacunacion">
                        <td>{{ vacuna.nombreVacuna }}</td>
                        <td>{{ vacuna.fecha }}</td>
                        <td>{{ vacuna.proximaFecha }}</td>
                      </tr>
                    </tbody>
                  </table>
                </div>


              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  </body>
</html>
