import { AfterViewInit, Component, ViewChild } from '@angular/core';
import {
  ScannerQRCodeConfig,
  ScannerQRCodeResult,
  NgxScannerQrcodeService,
  NgxScannerQrcodeComponent,
  ScannerQRCodeSelectedFiles,
} from 'ngx-scanner-qrcode';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements AfterViewInit {
  title = "DieLess";

  // https://developer.mozilla.org/en-US/docs/Web/API/MediaDevices/getUserMedia#front_and_back_camera
  public config: ScannerQRCodeConfig = {
    constraints: {
      video: {
        width: window.innerWidth
      },
    },
  };

  //holds results
  public qrCodeResult: ScannerQRCodeSelectedFiles[] = [];
  public qrCodeResult2: ScannerQRCodeSelectedFiles[] = [];

  //reference to the component
  @ViewChild('action') action!: NgxScannerQrcodeComponent;

  //injects the service
  constructor(private qrcode: NgxScannerQrcodeService) { }

  //coment the handle to start automatically
  ngAfterViewInit(): void {
    this.action.isReady.subscribe((res: any) => {
       this.handle(this.action, 'start');
    });
  }

  //when a event is emited to the scanner logs it into the console
  //the line above console pauses once it gets a qr
  public onEvent(e: ScannerQRCodeResult[], action?: any): void {
    e && action && action.pause();
    console.log(e);
  }

  //USED TO START STOP PLAY OR PAUSE THE SCANNER
  public handle(action: any, fn: string): void {
    const playDeviceFacingBack = (devices: any[]) => {
      // front camera or back camera check here!
      const device = devices.find(f => (/back|rear|environment/gi.test(f.label))); // Default Back Facing Camera
      action.playDevice(device ? device.deviceId : devices[0].deviceId);
    }

    if (fn === 'start') {
      action[fn](playDeviceFacingBack).subscribe((r: any) => console.log(fn, r), alert);
    } else {
      action[fn]().subscribe((r: any) => console.log(fn, r), alert);
    }
  }

  //to upload files meh
  public onSelects(files: any) {
    this.qrcode.loadFiles(files, 0.5).subscribe((res: ScannerQRCodeSelectedFiles[]) => {
      this.qrCodeResult = res;
    });
  }

  public onSelects2(files: any) {
    this.qrcode.loadFilesToScan(files, this.config, 0.5).subscribe((res: ScannerQRCodeSelectedFiles[]) => {
      console.log(res);
      this.qrCodeResult2 = res;
    });
  }
}

<html>
  <body style="background-color: #c2d1ff">
    <div>
      <section class="py-6">
        <div class="container">
          <div class="row">
            <div class="col-sm-12 text-center">
              <h2 class="sm-3">DIELESS</h2>
              <h4 class="sm-4 secondary_color">
                Bienvenidos a tu portal de plantillas de vacunación DieLess.
                <br />
                Nuestro sistema innovador le permite escanear un código QR para
                obtener información detallada sobre la vacunación de su perro.
              </h4>

              <div class="col-sm-12">
                <div class="photo_qr_card">
                  <img src="..\assets\cat1.jpg" alt="cat1" class="img-fluid" />
                </div>
              </div>
            </div>

            <div class="col-sm-12">
              <div class="mt-5">
                <a
                  href="https://www.youtube.com/watch?v=maYniX-PAUQ"
                  class="button btn-primary btn-px-2 btn-lg m-auto"
                  target="_blank"
                  ><span class="icon-play2 mr-3"> </span>¡Escanear OTRO
                  Codigo!</a
                >
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
    <!-- Scanner -->
    <div>
      <!-- ngx-scanner-qrcode -->
      <ngx-scanner-qrcode
        #action="scanner"
        [config]="config"
        (event)="onEvent($event, action)"
      ></ngx-scanner-qrcode>

      <!-- data  -->
      <!-- <p class="data">{{ (action.data | async | json) }}</p> -->

      <!-- loading -->
      <p *ngIf="action.isLoading">⌛ Loading...</p>

      <!-- start/stop -->
      <button
        class="btn"
        [class.btn-info]="!action.isStart"
        [class.btn-warning]="action.isStart"
        [disabled]="action.isLoading"
        (click)="handle(action, action.isStart ? 'stop' : 'start')"
      >
        <img
          [src]="
            action.isStart
              ? 'https://id1945.github.io/images/svg/camera-off.svg'
              : 'https://id1945.github.io/images/svg/camera-on.svg'
          "
          width="30px"
        />
      </button>

      <!-- play/pause -->
      <button
        class="btn"
        [class.btn-info]="!action.isStart"
        [class.btn-warning]="action.isStart"
        [disabled]="!action.isStart"
        (click)="handle(action, action.isPause ? 'play' : 'pause')"
      >
        <img
          [src]="
            action.isPause
              ? 'https://id1945.github.io/images/svg/play.svg'
              : 'https://id1945.github.io/images/svg/pause.svg'
          "
          width="30px"
        />
      </button>
      </div>
  </body>
</html>
